import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  // Check if user is logged in on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setIsLoggedIn(true);
      setUser(JSON.parse(userData));
      fetchBooks(token);
    }
  }, []);

  // Fetch books from backend
  const fetchBooks = async (token) => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/books', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setBooks(data.books || data);
      }
    } catch (error) {
      console.error('Error fetching books:', error);
    }
    setLoading(false);
  };

  // Handle search
  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/books?search=${searchTerm}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setBooks(data.books || data);
      }
    } catch (error) {
      console.error('Error searching books:', error);
    }
    setLoading(false);
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    setUser(null);
    setBooks([]);
  };

  // If not logged in, show login page
  if (!isLoggedIn) {
    return <LoginPage onLogin={(userData, token) => {
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setIsLoggedIn(true);
      setUser(userData);
      fetchBooks(token);
    }} />;
  }

  // Logged in - show main app
  return (
    <div className="App">
      <nav className="navbar">
        <div className="nav-brand">ðŸ“š ESSSL Library</div>
        <div className="nav-menu">
          <span className="welcome-text">Welcome, {user?.name}</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </nav>

      <div className="main-container">
        <aside className="sidebar">
          <ul className="sidebar-menu">
            <li className="active">ðŸ“– Books</li>
            <li>ðŸ‘¤ Profile</li>
            <li>ðŸ“‹ Borrowings</li>
            {user?.role === 'librarian' && (
              <>
                <li>âž• Add Book</li>
                <li>ðŸ‘¥ Students</li>
                <li>ðŸ“Š Reports</li>
              </>
            )}
          </ul>
        </aside>

        <main className="content">
          <div className="search-section">
            <h2>Books Collection</h2>
            <div className="search-box">
              <input
                type="text"
                placeholder="Search by title, author..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button onClick={handleSearch}>Search</button>
            </div>
          </div>

          {loading ? (
            <div className="loading">Loading books...</div>
          ) : (
            <div className="books-grid">
              {books.length > 0 ? (
                books.map(book => (
                  <div key={book._id} className="book-card">
                    <div className="book-cover">ðŸ“˜</div>
                    <h3>{book.title}</h3>
                    <p className="book-author">by {book.author}</p>
                    <p className="book-isbn">ISBN: {book.isbn}</p>
                    <p className="book-category">{book.category}</p>
                    <div className="book-availability">
                      Available: {book.availableCopies}/{book.totalCopies}
                    </div>
                    {book.availableCopies > 0 && (
                      <button className="borrow-btn">Borrow</button>
                    )}
                  </div>
                ))
              ) : (
                <p className="no-books">No books found</p>
              )}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

// Login Page Component
function LoginPage({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        onLogin(data, data.token);
      } else {
        setError(data.message || 'Login failed');
      }
    } catch (error) {
      setError('Error connecting to server');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>ðŸ“š ESSSL Library System</h1>
        <h2>Login to your account</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          
          <button type="submit">Login</button>
        </form>
        
        <div className="demo-credentials">
          <p><strong>Demo Credentials:</strong></p>
          <p>ðŸ“§ librarian@esssl.edu / admin123</p>
          <p>ðŸ“§ alice@esssl.edu / student123</p>
        </div>
      </div>
    </div>
  );
}

export default App;
